// DepartmentDashboard.js
import React, { useState } from 'react';
import ComplaintForm from './ComplaintForm';
import ComplaintsTable from './ComplaintsTable';
import DashHeader from './DashHeader';

function DepartmentDashboard() {
    const [showForm, setShowForm] = useState(false);
    const [complaints, setComplaints] = useState([]);
    const [showPreview, setShowPreview] = useState(false);
    const [previewData, setPreviewData] = useState(null);

    const handleFormSubmit = (complaint) => {
        setPreviewData(complaint); // Set data for preview
        setShowPreview(true); // Show preview
        setShowForm(false); // Hide form
    };

    const toggleFormVisibility = () => {
        setShowForm(!showForm);
        setShowPreview(false); // Hide preview when form is toggled
    };

    const closeForm = () => {
        setShowForm(false);
        setShowPreview(false); // Hide preview when form is closed
    };

    const handleFinalSubmit = () => {
        // Submit logic here
        setComplaints([...complaints, previewData]); // Update complaints with preview data
        setShowPreview(false); // Hide preview after final submission
    };

    return (
        <div>
            <DashHeader/>
            <div className={`dashboard ${showForm ? 'overlay-active' : ''}`}>
                {showForm && (
                    <div className="form-overlay" onClick={closeForm}>
                        <ComplaintForm onSubmit={handleFormSubmit} />
                    </div>
                )}
                <div className="dashboard-container">
                    <h1>Complaints</h1>
                    <ComplaintsTable complaints={complaints} />
                    <button onClick={toggleFormVisibility}>Add New Complaint</button>
                </div>
            </div>
            {showPreview && (
                <div className="preview-overlay">
                    <ComplaintsTable complaints={[previewData]} /> {/* Render preview data */}
                    <button onClick={handleFinalSubmit}>Confirm and Submit</button>
                </div>
            )}
        </div>
    );
}

export default DepartmentDashboard;
