import React from 'react';
import { Page, Text, View, Document, StyleSheet, Image } from '@react-pdf/renderer';

const styles = StyleSheet.create({
    page: {
        padding: 30,
    },
    header: {
        marginBottom: 20,
        paddingBottom: 10,
        borderBottom: '2px solid #000',
    },
    title: {
        fontSize: 24,
        textAlign: 'center',
    },
    section: {
        marginBottom: 10,
    },
    label: {
        fontSize: 12,
        fontWeight: 'bold',
    },
    value: {
        fontSize: 12,
        marginBottom: 5,
    },
    footer: {
        marginTop: 20,
        paddingTop: 10,
        borderTop: '1px solid #000',
        textAlign: 'center',
        fontSize: 10,
    },
});

const ComplaintPDF = ({ complaint }) => (
    <Document>
        <Page size="A4" style={styles.page}>
            <View style={styles.header}>
                <Text style={styles.title}>Complaint Details</Text>
            </View>
            <View style={styles.section}>
                <Text style={styles.label}>Title:</Text>
                <Text style={styles.value}>{complaint.title}</Text>
            </View>
            <View style={styles.section}>
                <Text style={styles.label}>Issue:</Text>
                <Text style={styles.value}>{complaint.issue}</Text>
            </View>
            <View style={styles.section}>
                <Text style={styles.label}>Location:</Text>
                <Text style={styles.value}>{complaint.location}</Text>
            </View>
            <View style={styles.section}>
                <Text style={styles.label}>Assigned Worker:</Text>
                <Text style={styles.value}>{complaint.assignedWorker || 'N/A'}</Text>
            </View>
            <View style={styles.section}>
                <Text style={styles.label}>Status:</Text>
                <Text style={styles.value}>{complaint.status}</Text>
            </View>
            <View style={styles.footer}>
                <Text>Generated by Complaint Management System</Text>
            </View>
        </Page>
    </Document>
);

export default ComplaintPDF;
