import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

function ComplaintForm({ userType }) {
    const [form, setForm] = useState({
        identifier: '', // student number or staff id
        title: '',
        issue: '',
        location: '',
        nature: '',
        priority: ''
    });
    const navigate = useNavigate();

    const handleChange = (e) => {
        setForm({ ...form, [e.target.name]: e.target.value });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        // Submit form logic here
        // You might want to add form validation before submitting
        console.log(form);
        // After submission, navigate to a confirmation page or dashboard
        navigate('/dashboard');
    };

    return (
        <div className="complaint-form-container">
            <h2>{userType === 'student' ? 'Student' : 'Staff'} Complaint Form</h2>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>
                        {userType === 'student' ? 'Student Number' : 'Staff ID'}:
                        <input
                            type="text"
                            name="identifier"
                            value={form.identifier}
                            onChange={handleChange}
                            required
                        />
                    </label>
                </div>
                {/* The rest of the form fields remain the same for both user types */}
                <div>
                    <label>Title:<input type="text" name="title" value={form.title} onChange={handleChange} required /></label>
                </div>
                <div>
                    <label>Issue:<textarea name="issue" value={form.issue} onChange={handleChange} required /></label>
                </div>
                <div>
                    <label>Location:<input type="text" name="location" value={form.location} onChange={handleChange} required /></label>
                </div>
                <div>
                    <label>Nature:<input type="text" name="nature" value={form.nature} onChange={handleChange} required /></label>
                </div>
                <div>
                    <label>Priority:<select name="priority" value={form.priority} onChange={handleChange} required>
                        <option value="low">Low</option>
                        <option value="medium">Medium</option>
                        <option value="high">High</option>
                    </select></label>
                </div>
                <button type="submit">Submit Complaint</button>
            </form>
        </div>
    );
}

export default ComplaintForm;
