import React from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import '../css/LoginPage.css'; // Make sure this is the correct path

function LoginPage() {
    const navigate = useNavigate();
    const { userType } = useParams(); // This will be undefined if you're using the direct path method

    const handleLogin = (event) => {
        event.preventDefault();
        // Perform login logic here
        // Redirect to the dashboard or next page based on userType
        navigate(`/${userType}/dashboard`); // Use a dynamic path based on userType
    };

    return (
        <div className="login-container">
            <form className="login-form" onSubmit={handleLogin}>
                <h2>Login ({userType})</h2>
                {/* ... rest of your login form */}
            </form>
        </div>
    );
}

export default LoginPage;
