import React, { useState } from 'react';
import ComplaintForm from '../ComplaintForm';
import ComplaintsTable from '../ComplaintsTable';
import DashHeader from '../DashHeader';
import './DepartmentDashboard.css';

const DepartmentDashboard = () => {
    const [showForm, setShowForm] = useState(false);
    const [complaints, setComplaints] = useState([]);

    const handleFormSubmit = (complaint) => {
        setComplaints([...complaints, complaint]);
        setShowForm(false);
    };

    const toggleFormVisibility = () => {
        setShowForm(!showForm);
    };

    const closeForm = () => {
        setShowForm(false);
    };

    const handleAssignWorkerAndStatus = (index, assignedWorker, status) => {
        const updatedComplaints = complaints.map((complaint, i) =>
            i === index ? { ...complaint, assignedWorker, status } : complaint
        );
        setComplaints(updatedComplaints);
    };

    const handleOverlayClick = (e) => {
        if (e.target.classList.contains('form-overlay')) {
            closeForm();
        }
    };

    return (
        <div>
            <DashHeader />
            <div className={`dashboard ${showForm ? 'overlay-active' : ''}`}>
                {showForm && (
                    <div className="form-overlay" onClick={handleOverlayClick}>
                        <ComplaintForm onSubmit={handleFormSubmit} />
                    </div>
                )}
                <div className="dashboard-container">
                    <h1>Complaints</h1>
                    <ComplaintsTable
                        complaints={complaints}
                        handleAssignWorkerAndStatus={handleAssignWorkerAndStatus}
                    />
                    <button onClick={toggleFormVisibility}>Add New Complaint</button>
                </div>
            </div>
        </div>
    );
};

export default DepartmentDashboard;
