import React, { useState } from 'react';
import '../css/ComplaintsTable.css';

const ComplaintsTable = ({ complaints, markAsDone }) => {
    const [sortedComplaints, setSortedComplaints] = useState(complaints);
    const [dropdownVisible, setDropdownVisible] = useState(false);

    const toggleDropdown = () => {
        setDropdownVisible(!dropdownVisible);
    };

    const sortComplaints = (criteria) => {
        const now = new Date();
        const sorted = [...complaints].sort((a, b) => {
            const dateA = new Date(a.date);
            const dateB = new Date(b.date);

            if (criteria === 'week') {
                return now - dateA <= 7 * 24 * 60 * 60 * 1000
                    ? now - dateB <= 7 * 24 * 60 * 60 * 1000 ? dateA - dateB : -1
                    : 1;
            } else if (criteria === 'month') {
                return now - dateA <= 30 * 24 * 60 * 60 * 1000
                    ? now - dateB <= 30 * 24 * 60 * 60 * 1000 ? dateA - dateB : -1
                    : 1;
            } else {
                return dateA - dateB;
            }
        });
        setSortedComplaints(sorted);
        setDropdownVisible(false);
    };

    return (
        <div>
            <div className="table-header">
                <div className="header-buttons">
                    <div className="dropdown">
                        <button onClick={toggleDropdown}>Sort</button>
                        {dropdownVisible && (
                            <div className="dropdown-menu">
                                <div className="dropdown-item" onClick={() => sortComplaints('week')}>Week</div>
                                <div className="dropdown-item" onClick={() => sortComplaints('month')}>Month</div>
                            </div>
                        )}
                    </div>
                    <button onClick={() => console.log('Add New Complaint')}>Add New Complaint</button>
                </div>
            </div>
            <table className="complaints-table">
                <thead>
                    <tr>
                        <th>Title</th>
                        <th>Issue</th>
                        <th>Location</th>
                        <th>Assigned Worker</th>
                        <th>Status</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {sortedComplaints.map((complaint, index) => (
                        <tr key={index}>
                            <td>{complaint.title}</td>
                            <td>{complaint.issue}</td>
                            <td>{complaint.location}</td>
                            <td>{complaint.assignedWorker}</td>
                            <td>{complaint.status}</td>
                            <td>
                                {complaint.status === 'In Progress' && (
                                    <button onClick={() => markAsDone(index)}>Mark as Done</button>
                                )}
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default ComplaintsTable;
