import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import '../css/DashHeader.css';

const DashHeader = () => {
    const [dropdownVisible, setDropdownVisible] = useState(false);
    const [username, setUsername] = useState('');
    const navigate = useNavigate();

    useEffect(() => {
        // Fetch the username from local storage or your state management
        const storedUsername = localStorage.getItem('username');
        if (storedUsername) {
            setUsername(storedUsername);
        }
    }, []);

    const toggleDropdown = () => {
        setDropdownVisible(!dropdownVisible);
    };

    const handleLogout = () => {
        localStorage.removeItem('token');
        localStorage.removeItem('username');
        navigate('/login/department');
    };

    return (
        <div className="header-container">
            <div className="header-logo">Dashboard</div>
            <ul className="header-nav">
                <li className="header-nav-item">Home</li>
                <li className="header-nav-item">View Complaints</li>
                <li className="header-nav-item">Reports</li>
                <li className="header-nav-item">Settings</li>
            </ul>
            <div className="header-user-section">
                <div className="header-user-avatar" style={{ backgroundImage: 'url(/path-to-avatar.jpg)' }}></div>
                <div className="header-user-name" onClick={toggleDropdown}>{username}</div>
                <button className="header-user-dropdown" onClick={toggleDropdown}>â–¼</button>
                {dropdownVisible && (
                    <div className="header-dropdown-menu">
                        <div className="header-dropdown-item">Profile</div>
                        <div className="header-dropdown-item" onClick={handleLogout}>Logout</div>
                    </div>
                )}
            </div>
        </div>
    );
}

export default DashHeader;
