// export default DepartmentDashboard;

function DepartmentDashboard() {
    const [complaints, setComplaints] = useState([]);
    const [showForm, setShowForm] = useState(false);

    const handleFormSubmit = (newComplaint) => {
        setComplaints([...complaints, newComplaint]);
        setShowForm(false); // Hide form after submission
    };

    const handleAssignWorkerAndStatus = (index, assignedWorker, status) => {
        const updatedComplaints = complaints.map((complaint, i) =>
            i === index ? { ...complaint, assignedWorker, status } : complaint
        );
        setComplaints(updatedComplaints);
    };

    return (
        <div className="department-dashboard">
            <h2>Department Dashboard</h2>
            <button onClick={() => setShowForm(true)}>Add Complaint</button>
            {showForm && (
                <ComplaintForm onSubmit={handleFormSubmit} />
            )}
            <ComplaintsTable 
                complaints={complaints} 
                handleAssignWorkerAndStatus={handleAssignWorkerAndStatus} 
            />
        </div>
    );
}

export default DepartmentDashboard;
