import React, { useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { Document, Page, Text, View, StyleSheet, Image, Font, PDFDownloadLink, BlobProvider } from '@react-pdf/renderer';
import '../css/ComplaintsForm.css';

// Styles for the PDF document
const styles = StyleSheet.create({
    page: {
        flexDirection: 'column',
        backgroundColor: '#fff',
        padding: 30
    },
    logo: {
        width: 150,
        margin: '0 auto'
    },
    title: {
        fontSize: 24,
        textAlign: 'center',
        margin: 10,
        // fontFamily: 'Open Sans' // Use this if you've registered a font
    },
    content: {
        fontSize: 16,
        margin: 10
    },
    footer: {
        position: 'absolute',
        fontSize: 12,
        bottom: 25,
        left: 30,
        right: 30,
        textAlign: 'center',
        color: 'grey',
    },
    seal: {
        width: 100,
        position: 'absolute',
        bottom: 25,
        left: 30,
    },
    signature: {
        width: 100,
        position: 'absolute',
        bottom: 25,
        right: 30,
    }
});

function ComplaintForm() {
    const { userType } = useParams();
    const navigate = useNavigate();
    const [complaintData, setComplaintData] = useState({
        identifier: '',
        title: '',
        issue: '',
        location: '',
        nature: '',
        phone: '',
        priority: '',
        image: null
    });
    const [submitted, setSubmitted] = useState(false);
    const [pdfBlobUrl, setPdfBlobUrl] = useState(null);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setComplaintData({ ...complaintData, [name]: value });
    };

    const handleImageChange = (e) => {
        setComplaintData({ ...complaintData, image: e.target.files[0] });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        setSubmitted(true);
    };

    const handleFinalSubmit = () => {
        // Here you should handle the logic to submit the complaint data to the server
        // For example, send a POST request with the complaintData state
        // Then, you might want to redirect the user to a different page or show a success message
        console.log("Final Submission", complaintData);

        const ComplaintDocument = () => (
            <Document>
                <Page size="A4" style={styles.page}>
                    <Image style={styles.logo} src="path_to_your_logo.png" />
                    <Text style={styles.title}>College Name - Department</Text>
                    {/* Repeat this for all your fields */}
                    <Text style={styles.content}>Identifier: {complaintData.identifier}</Text>
                    {/* ... */}
                    <Image style={styles.seal} src="path_to_your_seal.png" />
                    <Text style={styles.footer}>Department Seal</Text>
                    <Image style={styles.signature} src="path_to_signature_placeholder.png" />
                    <Text style={styles.footer}>Estate Office Sign</Text>
                </Page>
            </Document>
        );

        return (
            <div className="complaint-form-container" onClick={(e) => e.stopPropagation()}>
                {!submitted ? (
                    <form onSubmit={handleSubmit} encType="multipart/form-data">
                        <div className="input-group">
                            <label htmlFor="identifier">Identifier</label>
                            <input
                                type="text"
                                id="identifier"
                                name="identifier"
                                value={complaintData.identifier}
                                onChange={handleChange}
                                required
                            />
                        </div>
                        <div className="input-group">
                            <label htmlFor="title">Title</label>
                            <input
                                type="text"
                                id="title"
                                name="title"
                                value={complaintData.title}
                                onChange={handleChange}
                                required
                            />
                        </div>
                        <div className="input-group">
                            <label htmlFor="issue">Issue</label>
                            <textarea
                                id="issue"
                                name="issue"
                                value={complaintData.issue}
                                onChange={handleChange}
                                required
                            />
                        </div>
                        <div className="input-group">
                            <label htmlFor="location">Location</label>
                            <input
                                type="text"
                                id="location"
                                name="location"
                                value={complaintData.location}
                                onChange={handleChange}
                                required
                            />
                        </div>
                        <div className="input-group">
                            <label htmlFor="nature">Nature of the Complaint</label>
                            <input
                                type="text"
                                id="nature"
                                name="nature"
                                value={complaintData.nature}
                                onChange={handleChange}
                                required
                            />
                        </div>
                        <div className="input-group">
                            <label htmlFor="phone">Phone Number</label>
                            <input
                                type="tel"
                                id="phone"
                                name="phone"
                                value={complaintData.phone}
                                onChange={handleChange}
                                required
                            />
                        </div>
                        <div className="input-group">
                            <label htmlFor="priority">Priority</label>
                            <select
                                id="priority"
                                name="priority"
                                value={complaintData.priority}
                                onChange={handleChange}
                                required
                            >
                                <option value="low">Low</option>
                                <option value="medium">Medium</option>
                                <option value="high">High</option>
                            </select>
                        </div>
                        <div className="input-group">
                            <label htmlFor="image">Image (Optional)</label>
                            <input
                                type="file"
                                id="image"
                                name="image"
                                onChange={handleImageChange}
                                accept="image/*"
                            />
                        </div>
                        <button type="submit">Submit Complaint</button>
                    </form>
                ) : (
                    <div>
                        <BlobProvider document={<ComplaintDocument />}>
                            {({ blob, url, loading, error }) => {
                                if (loading) {
                                    return <div>Loading PDF...</div>;
                                }
                                setPdfBlobUrl(url); // Save the URL for the PDF blob
                                return (
                                    <div>
                                        <iframe src={url} style={{ width: '100%', height: '500px' }} title="pdf-preview" />
                                        <PDFDownloadLink document={<ComplaintDocument />} fileName="ComplaintDetails.pdf">
                                            {({ loading }) => (loading ? 'Preparing document...' : 'Download PDF')}
                                        </PDFDownloadLink>
                                        <button onClick={() => setSubmitted(false)}>Edit</button>
                                        <button onClick={handleFinalSubmit}>Confirm and Submit</button>
                                    </div>
                                );
                            }}
                        </BlobProvider>
                    </div>
                )}
            </div>
        );
    }
}
export default ComplaintForm;
