import React, { useState, useEffect } from 'react';
import axios from 'axios';
// import ComplaintForm from './ComplaintForm';
// import ComplaintsTable from './ComplaintsTable';
// import DashHeader from './DashHeader';
import '../css/DepartmentDashboard.css';

const DepartmentDashboard = () => {
    const [showForm, setShowForm] = useState(false);
    const [complaints, setComplaints] = useState([]);

    useEffect(() => {
        fetchComplaints();
    }, []);

    const fetchComplaints = async () => {
        try {
            const res = await axios.get('http://localhost:5000/api/complaints');
            setComplaints(res.data);
        } catch (error) {
            console.error('There was an error fetching the complaints!', error);
        }
    };

    const handleFormSubmit = (newComplaint) => {
        setComplaints([...complaints, newComplaint]);
        setShowForm(false);
    };

    const toggleFormVisibility = () => {
        setShowForm(!showForm);
    };

    return (
        <div>
            <DashHeader />
            <div className={`dashboard ${showForm ? 'overlay-active' : ''}`}>
                {showForm && (
                    <div className="form-overlay" onClick={() => setShowForm(false)}>
                        <ComplaintForm onSubmit={handleFormSubmit} />
                    </div>
                )}
                <div className="dashboard-container">
                    <h1>Complaints</h1>
                    <ComplaintsTable complaints={complaints} setComplaints={setComplaints} />
                    <button onClick={toggleFormVisibility}>Add New Complaint</button>
                </div>
            </div>
        </div>
    );
};

export default DepartmentDashboard;
