import React, { useState } from 'react';
import ComplaintForm from '../ComplaintForm';
import ComplaintsTable from '../ComplaintsTable';
import './DepartmentDashboard.css';

const DepartmentDashboard = () => {
    const [showForm, setShowForm] = useState(false);
    const [complaints, setComplaints] = useState([]);

    const handleFormSubmit = (complaint) => {
        setComplaints([...complaints, complaint]);
        setShowForm(false);
    };

    // const handleBackgroundClick = (e) => {
    //     // Check if the click is outside the form element
    //     if (e.target.className === "form-modal") {
    //         setShowForm(false);
    //     }
    // };

    const closeForm = () => setShowForm(false);

    return (
        <div className="dashboard">
            {showForm && <div className="overlay" onClick={closeForm}></div>}
            <div className={`dashboard-container ${showForm ? 'blur' : ''}`}>
                <h1>Department Dashboard</h1>
                <button className="add-complaint-button" onClick={() => setShowForm(true)}>
                    <span className="plus-icon">+</span> Add New Complaint
                </button>
                {showForm && (
                    <div className="form-modal">
                        <ComplaintForm onSubmit={handleFormSubmit} />
                    </div>
                )}
                <ComplaintsTable complaints={complaints} />
            </div>
        </div>
    );
}

export default DepartmentDashboard;
